import React, { useEffect } from 'react';
import { useNavigate } from "react-router-dom";
import { useState } from 'react';
import NewsList from './NewsList';
import AdminRouteProtection from './AdminRouteProtection';

const AdminNews = () => {

    AdminRouteProtection();
    let navigate = useNavigate();
    
    var [news_articles, setNewsArticles] = useState(null);
    var [isPending, setIsPending] = useState(true);
    var [error, setError] = useState(null);
    
    useEffect(() => {
        setTimeout(() => {
            fetch('http://localhost:8000/news_articles')
          .then(res => {
            if (!res.ok) { // Handle server errors
              throw Error('could not fetch the data for that resource');
            } 
            return res.json();
          })
          .then(data => {
            setIsPending(false);
            setNewsArticles(data);
            setError(null);
          })
          .catch(err => {
            setIsPending(false);
            setError(err.message); // Handle Fetch Errors
          })
        }, 1000);
      }, [])

    return (
        <div>
            <h1>News Articles Page Admin</h1>
            <p><button onClick={() => {
                    navigate("/");
                }}>Main Page</button></p>
            <p><button onClick={() => {
                    navigate("/HomeAdmin");
                }}>Home Admin</button></p>
            <p></p>
            {error && <div>{ error } </div>}
            {isPending && <div>Loading News...</div>}
            {news_articles && <NewsList news_articles={news_articles} />}
        </div>
    )
}

export default AdminNews;
