import React from 'react';
import { useAuth0 } from "@auth0/auth0-react";
import HomeAdmin from './HomeAdmin'
import { useNavigate, Link } from "react-router-dom";
import Profile from './Profile';

const IsAuthAndRole = () => {
    const { user, isAuthenticated } = useAuth0();
    var role = "user"
    var sub = ""
    if (isAuthenticated && user.sub != null) // Get auth0 unique user id
        sub = user.sub
    if (isAuthenticated && sub == "google-oauth2|108756263942986646301") // Check id to see if it's in the list of admins
        role = "admin"
    
    let navigate = useNavigate();
    let isProfileclicked = false;

    if(isAuthenticated && role == "admin")
        return (
            isAuthenticated && <div>
                <p>User Authenticated</p>
                <p>User Role : {role}</p>
                <button onClick={() => {
                    navigate("/HomeAdmin");
                }}>AdminHub</button>

                <button onClick={() => {isProfileclicked = true;}}>View Account Details</button>
                {/* {isProfileclicked == true ? return(<Profile />); : x} */}
                { isProfileclicked ? <Profile /> : null }

            </div>
        )
    if(isAuthenticated && role == "user")
        return (
            isAuthenticated && <div>
                <p>User Authenticated</p>
                <p>User Role : {role}</p>
            </div>
        )
    return (
        <div>
            <p>User Not Authenticated</p>
        </div>
    ) 
}

export default IsAuthAndRole;
