import React from 'react';
import { useAuth0 } from "@auth0/auth0-react";
import JSONPretty from 'react-json-pretty';
import axios from "axios";

const Profile = () => {
    const { user, isAuthenticated } = useAuth0();
    var role = "user"
    var sub = ""
    if (isAuthenticated && user.sub != null)
        sub = user.sub
    if (isAuthenticated && user.email == "andygabi99@gmail.com" && sub == "google-oauth2|108756263942986646301") // Hardcoded admin
        role = "admin"

    // var roles = this.user['https://ian:auth0:com/roles']
    //     this.roles = roles[0]

    var client = new ManagementApiClient("TOKEN", new Uri("https://dev-i4636z4k.eu.auth0.com/api/v2/"));

    // return client.Users.GetAsync(USERID).Result;

    return (
        isAuthenticated && <div>
            <JSONPretty data={user} />
            <p>{user.email}</p>
            <p>{sub}</p>
            <p>{role}</p>
        </div>
    ) 
}

export default Profile;
