import { useNavigate } from "react-router-dom";
import { useAuth0 } from '@auth0/auth0-react';
import FetchAdminCheck from "./FetchAdminCheck";

const UserRouteProtection = () => {

const { user, isAuthenticated } = useAuth0();
let navigate = useNavigate();
var role = "user"
var sub = ""
if (isAuthenticated && user.sub != null) // Get auth0 unique user id
    sub = user.sub
if (isAuthenticated && sub == "google-oauth2|108756263942986646301") // Check id to see if it's in the list of admins
    role = "admin"

if (!isAuthenticated)
    return (
        <div>
            {navigate("/AccessDenied")}
        </div>
    )
if (isAuthenticated && role == "admin")
    return (
        <div>
            {navigate("/AccessDenied")}
        </div>
    )

}
export default UserRouteProtection;
