import React, { useEffect } from 'react';
import { useNavigate } from "react-router-dom";
import { useState } from 'react';
import RequestsList from './RequestsList';
import AdminRouteProtection from './AdminRouteProtection';

const AdminRequests = () => {

    AdminRouteProtection();
    let navigate = useNavigate();
    
    const [requests, setRequests] = useState(null);
    const [isPending, setIsPending] = useState(true);
    const [error, setError] = useState(null);
    
    useEffect(() => {
        setTimeout(() => {
            fetch('http://localhost:8000/requests')
          .then(res => {
            if (!res.ok) { // Handle server errors
              throw Error('could not fetch the data for that resource');
            } 
            return res.json();
          })
          .then(data => {
            setIsPending(false);
            setRequests(data);
            setError(null);
          })
          .catch(err => {
            setIsPending(false);
            setError(err.message); // Handle Fetch Errors
          })
        }, 1000);
      }, [])


    return (
        <div>
            <h1>News Articles Page Admin</h1>
            <p><button onClick={() => {
                    navigate("/");
                }}>Main Page</button></p>
            <p><button onClick={() => {
                    navigate("/HomeAdmin");
                }}>Home Admin</button></p>
            <p></p>
            {error && <div>{ error } </div>}
            {isPending && <div>Loading Requests...</div>}
            {requests && <RequestsList requests={requests} />}
        </div>
    )
}

export default AdminRequests;
