import React from 'react';
import { useNavigate } from "react-router-dom";
import { useState } from 'react';
import AdminRouteProtection from './AdminRouteProtection';

const AdminSafeLocationsAdd = () => {
  
  AdminRouteProtection();
  let navigate = useNavigate();

    const [safe_spot_address, setTitle] = useState('');
    const [recommended_for, setBody] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        const safe_location = { safe_spot_address, recommended_for};
    
        fetch('http://localhost:8000/safe_locations', {
          method: 'POST',
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(safe_location)
        }).then(() => {
          console.log('safe location created and posted to the db');
          {navigate("/AdminSafeLocations")}
        })
      }
    
    return (
        <div>
            <h1>Add Safe Location Page Admin</h1>
            <p><button onClick={() => {
                    navigate("/");
                }}>Main Page</button></p>
            <p><button onClick={() => {
                    navigate("/HomeAdmin");
                }}>Home Admin</button></p>
            <p></p>

        <form onSubmit={handleSubmit}>
        <label>News Article Title:</label>
        <input 
          type="text" 
          required 
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <p></p>
        <label>News Article Body:</label>
        <textarea
          required
          value={body}
          onChange={(e) => setBody(e.target.value)}
        ></textarea>
        <p></p>
        <label>News Article Author:</label>
        <textarea
          required
          value={author}
          onChange={(e) => setName(e.target.value)}
        ></textarea>
        <p></p>
        <button>Add News Article</button>
        </form>
        </div>
    )
}

export default AdminSafeLocationsAdd;
