import React from 'react';
import { useAuth0 } from "@auth0/auth0-react";
import { useNavigate } from "react-router-dom";
import FetchAdminCheck from './FetchAdminCheck';

const IsAuthAndRole = () => {
    const { user, isLoading, isAuthenticated } = useAuth0();
    window.$role = "loading"
    var sub = ""
    if (isAuthenticated && user.sub != null) // Get auth0 unique user id
        sub = user.sub
    var adminrole = ''
    if (isAuthenticated) {
        adminrole = FetchAdminCheck();
    };
    if (isAuthenticated && adminrole == true && !isLoading) // Check id to see if it's in the list of admins
        window.$role = "admin";
    if (isAuthenticated && adminrole == false && !isLoading)
        window.$role = "user";

    if (isLoading) return <div>Loading Auth Status...</div>
    let navigate = useNavigate();

    if(isAuthenticated && window.$role == "admin")
        return (
            isAuthenticated && <div>
                {/* <p>User Authenticated as Admin</p>
                <p>Proceed to Admin Main Page</p> */}
                {/* <button onClick={() => { */}
                {navigate("/HomeAdmin")};
                {/* }}>Admin Main Page</button> */}
            </div>
        )
    if(isAuthenticated && window.$role == "user")
        return (
            isAuthenticated && <div>
                {/* <p>User Authenticated as User</p>
                <p>Proceed to User Main Page</p> */}
                {/* <button onClick={() => { */}
                {navigate("/HomeUser")};
                {/* }}>User Main Page</button> */}
            </div>
        )
    // if(isAuthenticated && window.$role == "loading") {
    //     return (
    //         isAuthenticated && <div>
    //         <p>User Authenticated</p>
    //     </div>
    //     )
    // }
    // return (
    //     <div>
    //         <p>User Not Authenticated</p>
    //     </div>
    // ) 
}

export default IsAuthAndRole;
