import React from 'react'
import { useAuth0 } from '@auth0/auth0-react';

function AccessDenied() {
    const { user, isAuthenticated } = useAuth0();
    var role = "user"
    var sub = ""
    if (isAuthenticated && user.sub != null) // Get auth0 unique user id
        sub = user.sub
    if (isAuthenticated && sub == "google-oauth2|108756263942986646301") // Check id to see if it's in the list of admins
        role = "admin"
    let navigate = useNavigate();

    if (!isAuthenticated)
        return (
            <div>
                <p>You need to Log In before accessing any of the pages</p>
                <p>Please return to our Main Page and Log In there</p>
                <p><button onClick={() => {
                    navigate("/");
                }}>Main Page</button></p>
                {navigate("/")}
            </div>
        )
    if (isAuthenticated && role == "user")
        return (
            <div>
                {navigate("/")}
            </div>
        )


    return (
        <div>
            404 Page Not Found
        </div>
    )
}

export default AccessDenied
