import React, { useEffect } from 'react';
import { useAuth0 } from "@auth0/auth0-react";
import { useState } from 'react';

const FetchAdminCheck = () => {
    const { user, isAuthenticated } = useAuth0();
    var role = "user"
    var sub = ""
    if (isAuthenticated && user.sub != null) // Get auth0 unique user id
        sub = user.sub

    const [admins, setAdmins] = useState(null);
    const [isPending, setIsPending] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
            setTimeout(() => {
                fetch('http://localhost:8000/admins')
              .then(res => {
                if (!res.ok) { // Handle server errors
                  throw Error('could not fetch the data for that resource');
                } 
                return res.json();
              })
              .then(data => {
                setIsPending(false);
                setAdmins(data);
                setError(null);
              })
              .catch(err => {
                setIsPending(false);
                setError(err.message); // Handle Fetch Errors
              })
            }, 1000);
          }, [])

    var admin_role = false;

    for(var i = 0; i < admins.length; i++)
    {
        if(admins[i].admin_id_auth0 == sub)
            admin_role = true;
    }

    if (isAuthenticated && admin_role == true) // Check id to see if it's in the list of admins
        role = "admin"

    return (
        isAuthenticated && setAdmins && <div>
            <p>User email : {user.email}</p>
            <p>User unique Auth0 ID : {sub}</p>
            <p>User Role : {role}</p>
        </div>
    ) 
}

export default FetchAdminCheck;
