import React from 'react'
import { useAuth0 } from '@auth0/auth0-react';
import { useNavigate } from "react-router-dom";

function AccessDenied() {
    const { user, isAuthenticated } = useAuth0();
    var role = "user"
    var sub = ""
    if (isAuthenticated && user.sub != null) // Get auth0 unique user id
        sub = user.sub
    if (isAuthenticated && sub == "google-oauth2|108756263942986646301") // Check id to see if it's in the list of admins
        role = "admin"
    let navigate = useNavigate();

    if (!isAuthenticated)
        return (
            <div>
                <p>You need to Log In before accessing any of the pages</p>
                <p>Please return to our Main Page and Log In there</p>
                <p><button onClick={() => {
                    navigate("/");
                }}>Main Page</button></p>
            </div>
        )
    if (isAuthenticated && role == "user")
        return (
            <div>
                <p>You tried to access this page or</p>
                <p>You tried to access pages which are not destined for your current account type</p>
                <p>Please Log In with an admin account to access admin pages</p>
                <p>If you got lost proceed back to our MainPage</p>
                <p><button onClick={() => {
                    navigate("/");
                }}>Main Page</button></p>
            </div>
        )
    if (isAuthenticated && role == "admin")
        return (
            <div>
                <p>You tried to access this page or</p>
                <p>You tried to access pages which are not destined for your current account type</p>
                <p>Please Log In with a user account to access user pages</p>
                <p>If you got lost proceed back to our MainPage</p>
                <p><button onClick={() => {
                    navigate("/");
                }}>Main Page</button></p>
            </div>
        )
    return (
        <div>
            <p>Have you lost your way?</p>
            <p><button onClick={() => {
                    navigate("/");
                }}>Main Page</button></p>
        </div>
    )
}

export default AccessDenied;
