import { useNavigate } from "react-router-dom";

const NewsList = ({ news }) => {

    let navigate = useNavigate();
    return (news &&
      <div className="news-list">
        {news.map(news_article => (
          <div className="news-preview" key={news_article.id} >
            <h2>{ news_article.title }</h2>
            <p>Author : {news_article.author}</p>
            <p>{news_article.body}</p>
            <p>
            <button onClick={() => {
                    fetch('http://localhost:8000/news_articles/' + news_article.id, {
                      method: 'DELETE'
                    }).then(() => {
                      {navigate("/HomeAdmin")}
                    }) 
                }}>Delete News Article</button> {/*Delete News Article of this id*/}
            </p>  
          </div>
        ))}
      </div>
    );
  }

export default NewsList;
