import React, { useEffect } from 'react';
import { useNavigate, Link } from "react-router-dom";
import { useAuth0 } from '@auth0/auth0-react';
import { useState } from 'react';
import FundraisersList from './FundraisersList';

const AdminFundraisers = () => {
    const { user, isAuthenticated } = useAuth0();
    let navigate = useNavigate();
    var role = "user"
    var sub = ""
    if (isAuthenticated && user.sub != null) // Get auth0 unique user id
        sub = user.sub
    if (isAuthenticated && sub == "google-oauth2|108756263942986646301") // Check id to see if it's in the list of admins
        role = "admin"

    if (!isAuthenticated)
        return (
            <div>
                {navigate("/AccessDenied")}
            </div>
        )
    if (isAuthenticated && role == "user")
        return (
            <div>
                {navigate("/AccessDenied")}
            </div>
        )

    const [fundraisers, setFundraisers] = useState(null);
    const [isPending, setIsPending] = useState(true);
    const [error, setError] = useState(null);
    
    useEffect(() => {
        setTimeout(() => {
            fetch('http://localhost:8000/fundraisers')
          .then(res => {
            if (!res.ok) { // error coming back from server
              throw Error('could not fetch the data for that resource');
            } 
            return res.json();
          })
          .then(data => {
            setIsPending(false);
            setFundraisers(data);
            setError(null);
          })
          .catch(err => {
            setIsPending(false);
            setError(err.message); // Handle Fetch Errors
          })
        }, 1000);
      }, [])



    return (
        <div>
            <h1>Fundraisers Page Admin</h1>
            <p><button onClick={() => {
                    navigate("/");
                }}>Main Page</button></p>
            <p><button onClick={() => {
                    navigate("/HomeAdmin");
                }}>Home Admin</button></p>
            <p></p>
            {error && <div>{ error } </div>}
            {isPending && <div>Loading Fundraisers...</div>}
            {fundraisers && <FundraisersList fundraisers={fundraisers} />}
        </div>
    )
}

export default AdminFundraisers;
