import React from 'react';
import { useNavigate, Link } from "react-router-dom";
import { useAuth0 } from '@auth0/auth0-react';
import LogoutButton from './LogoutButton';
import Profile from './Profile';

const HomeUser = () => {
    const { user, isAuthenticated } = useAuth0();
    const [isProfileclicked, setProfileclicked] = React.useState(false);
    let navigate = useNavigate();
    var role = "user"
    var sub = ""
    if (isAuthenticated && user.sub != null) // Get auth0 unique user id
        sub = user.sub
    if (isAuthenticated && sub == "google-oauth2|108756263942986646301") // Check id to see if it's in the list of admins
        role = "admin"

    if (!isAuthenticated)
        return (
            <div>
                {navigate("/AccessDenied")}
            </div>
        )
    if (isAuthenticated && role == "admin")
        return (
            <div>
                {navigate("/AccessDenied")}
            </div>
        )
    return (
        <div>
            <h1>Home User Page</h1>
            <p><button onClick={() => {
                    navigate("/");
                }}>Main Page</button></p>
            <LogoutButton />
            <p></p>
            <button onClick={() => {isProfileclicked ? setProfileclicked(false) : setProfileclicked(true)}}>View Account Details</button>
            { isProfileclicked ? <Profile /> : null }
        </div>
    )
}

export default HomeUser;
