import React from 'react';
import { useAuth0 } from "@auth0/auth0-react";
import { useNavigate } from "react-router-dom";
import FetchAdminCheck from './FetchAdminCheck';

const IsAuthAndRole = async () => {
    const { user, isLoading, isAuthenticated } = useAuth0();
    var role = "user"
    var sub = ""
    if (isAuthenticated && user.sub != null) // Get auth0 unique user id
        sub = user.sub
    // if (isAuthenticated && sub == "google-oauth2|108756263942986646301") // Check id to see if it's in the list of admins
    //     role = "admin"
    if (isAuthenticated) {
        var adminrole = await FetchAdminCheck();
    };
    if (isAuthenticated && adminrole == true && !isLoading) // Check id to see if it's in the list of admins
        role = "admin";

    if (isLoading) return <div>Loading Auth Status...</div>
    let navigate = useNavigate();

    if(isAuthenticated && role == "admin")
        return (
            isAuthenticated && <div>
                <p>User Authenticated as Admin</p>
                <p>Proceed to Admin Main Page</p>
                <button onClick={() => {
                    navigate("/HomeAdmin");
                }}>Admin Main Page</button>
            </div>
        )
    if(isAuthenticated && role == "user")
        return (
            isAuthenticated && <div>
                <p>User Authenticated as User</p>
                <p>Proceed to User Main Page</p>
                <button onClick={() => {
                    navigate("/HomeUser");
                }}>User Main Page</button>
            </div>
        )
    return (
        <div>
            <p>User Not Authenticated</p>
        </div>
    ) 
}

export default IsAuthAndRole;
