import React, { useEffect } from 'react';
import { useNavigate } from "react-router-dom";
import { useState } from 'react';
import UserRouteProtection from './UserRouteProtection';
import { useAuth0 } from "@auth0/auth0-react";

const UserRequestsAdd = () => {
  
  UserRouteProtection();
  let navigate = useNavigate();
  const { user, isAuthenticated } = useAuth0();
  if(isAuthenticated) {
    personal_name = user.name;
    email = user.email;
    auth0_id = user.sub;
  }

    const [request, setRequest] = useState('');
    const [nr_telefon, setTelefon] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        const requestform = { personal_name, request, email, auth0_id, nr_telefon };
    
        fetch('http://localhost:8000/requests', {
          method: 'POST',
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(requestform)
        }).then(() => {
          console.log('request created and posted to the db');
          {navigate("/HomeUser")}
        })
      }
    
    return (
        <div>
            <h1>Submit Request</h1>
            <p><button onClick={() => {
                    navigate("/");
                }}>Main Page</button></p>
            <p><button onClick={() => {
                    navigate("/HomeUser");
                }}>Home User</button></p>
            <p></p>

        <form onSubmit={handleSubmit}>
        <label>Request :</label>
        <textarea
          required
          value={request}
          onChange={(e) => setRequest(e.target.value)}
        ></textarea>
        <p></p>
        <label>Telephone Number - optional:</label>
        <textarea
          required
          value={nr_telefon}
          onChange={(e) => setTelefon(e.target.value)}
        ></textarea>
        <p></p>
        <button>Send Request</button>
        </form>
        </div>
    )
}

export default UserRequestsAdd;
